
def main():
    ## Set the variables/constants
    input = "mjqjpqmgbljsphdztnvjfqwrcgsmlb"
    input = ""
    marker = 1
    start = 0
    
    ## Iterate over the input, char by char
    for signal in input:
        ## Check if the slice is N distinct chars long
        if (marker - start) == 4:
            break
        subs = input[start:marker]
        ## Check if the next char already exists in the slice
        if input[marker] in subs:
            start += subs.index(input[marker]) + 1
        marker += 1

    ## Ouput the final result
    print("First start-of-packet marker: {}".format(marker))

if __name__ == '__main__':
    main()
